import React,{useState} from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
export default function Mymodal() {
const [info,setInfo]= useState({
    modal:false,
    name:'',
    city:'',
    country:''
});
 function toggle(){
setInfo({modal:!info.modal});
  }
  function handleSubmit(e){
e.preventDefault();
console.log(info);
  }
  function changeValue(ev){
    setInfo({...info,[ev.target.name]:ev.target.value})
  }
  this.toggle();
  
    return (
    <div>
     
      <h1>React Bootstrap Modal Example</h1>
         <Button onClick={toggle}>clilck here</Button>
   <Modal isOpen={info.modal}> 
   <ModalHeader>modal header </ModalHeader>
   <ModalBody>
    <form onSubmit={handleSubmit}>
        <label>Name :</label>
        <input type="text" name='name'onChange={changeValue}  className='form-control'/><br></br>
        <label>email :</label>
        <input type="text" name='city' onChange={changeValue} className='form-control'/><br></br>
        <input type="submit" className='form-control'/>
    </form>
   </ModalBody>
   </Modal>

    </div>
  )
}
